          instance.close();
          break;
        case 'right':
          Dialog.confirm({
            title: '确定要删除该收藏记录？'
          }).then(() => {
            let index = instance.$el.attributes["data-i"].nodeValue
            // instance.close();
            that.del(index);
          }).catch(() => {
            // on cancel
          });
          break;
      }
    },
    del(index){
      let data = {
        reqbase:{
          timestamp:common.getLastDate(),
          clientauthflag:common.getClientauthflag(),
          reqorigin:"xuantie",
          token:common.getToken(),
          sourceip:common.getIp()
        },
        reqpage:{},
        reqparam:{
          userId:this.user.memberId,
          roomId: global.studioId
        }
      }
      let that = this
      http.postmain(api.addOrNo,data).then((response) => {
        if(response.data.respbase.returncode == '10000'){
          that.data.splice(index,1)
        }else{
          Toast({
            message: response.data.respbase.returnmsg,
            position: 'middle',
            duration: 2000
          })
        }
      })
    },
    todetail(id){
      this.$router.push({name:'livedetail',params:{id:id}})
    }
  }
}



