import http from '../../utils/http'
import api from '../../utils/api'
import common from '../../utils/common'
import Head from '../../components/head.vue'
import { Toast } from 'mint-ui'
export default{
  name: "fans",
  data(){
    return{
      page:1,
      data:[],
      title:'我的粉丝',
      isLoading:false, //是否显示加载中
      isMoreLoading:false, //是否加载更多
      noMore:false //是否还有更多
    }
  },
  // 计算属性
  computed: {
    user() {
      return this.$store.getters.getUser
    }
  },
  components:{
    Head
  },
  mounted(){
    // var that = this
    // setTimeout(() => {
    //   that.fetchData()
    // },500)
  },
  activated(){
    var that = this
    if(sessionStorage.user){
      setTimeout(() => {
        that.fetchData(true)
      },500)
    }else{
      this.$router.push({name:'sign'})
    }
  },
  deactivated(){
    // console.log("deactivated")
  },
  methods:{
    fetchData(isFirst){
      let model = {
        reqbase:{
          timestamp: common.getLastDate(),
          clientauthflag: common.getClientauthflag(),
          reqorigin: "xuantie",
          token: common.getToken(),
          sourceip: common.getIp()
        },
        reqpage:{
          total:0,
          page: this.page,
          size: 10,
          count: true
        },
        reqparam:{
          uid:this.user.memberId
        }
      }
      let that = this
      http.postmain(api.getFenSiList,model).then((response) => {
        if(isFirst){
          if(response.data.respbase.returncode == '10000'){
            if(response.data.respparam.length == 0){
              that.noMore = true
            }else{
              that.noMore = false
              that.data = response.data.respparam
            }
          }else{
            console.log("出错")
          }
        }else{
          that.isLoading = false
          if(response.data.respbase.returncode == '10000'){
            if(response.data.respparam.length == 0){
              that.noMore = true
            }else{
              that.isMoreLoading = false
              that.data = that.data.concat(response.data.respparam)
            }
          }else{
            console.log("出错")
          }
        }
      })
    },
    loadMore(){
      this.isMoreLoading = true // 设置加载更多中
      this.isLoading = true // 加载中转圈圈
      this.page++
      var that = this
      setTimeout(() => {
        that.fetchData(false)
      },1000)
    },
    follow(e,tid){
      e.stopPropagation();
      let data = {
        reqbase:{
          timestamp:common.getLastDate(),
          clientauthflag:common.getClientauthflag(),
          reqorigin:"xuantie",
          token:common.getToken(),
          sourceip:common.getIp()
        },
        reqpage:{},
        reqparam:{
          tid:tid,
          type:'1',
          uid:this.user.memberId
        }
      }
      var that = this
      http.postmain(api.attentionOrNo,data).then((response) => {
        if(response.data.respbase.returncode == '10000'){
          let obj = that.data
          for (var i = 0; i < obj.length; i++) {
            if(obj[i].uid == tid){
              if(obj[i].focusOn == false){
                Toast({
                  message: '关注成功',
                  position: 'middle',
                  duration: 2000
                })
                obj[i].focusOn = true
              }else{
                Toast({
                  message: '取消关注',
                  position: 'middle',
                  duration: 2000
                })
                obj[i].focusOn = false
              }
            }
          }
          that.data = obj
          //更新期问列表
          let obj1 = that.$store.getters.getQuestionData
          for (var i = 0; i < obj1.length; i++) {
            if(obj1[i].memberId == tid){
              if(obj1[i].isFollowVip == 0){
                Toast({
                  message: '关注成功',
                  position: 'middle',
                  duration: 2000
                })
                obj1[i].isFollowVip = 1
              }else{
                Toast({
                  message: '取消关注',
                  position: 'middle',
                  duration: 2000
                })
                obj1[i].isFollowVip = 0
              }
            }
          }
          that.$store.commit('resetQuestionData',obj1)
        }else{
          Toast({
            message: response.data.respbase.returnmsg,
            position: 'middle',
            duration: 2000
          })
        }
      })
    }
  }
}



