      if(!val){
        val = val == 0 ? '男' : '女'
      }else{
        val = "女"
      }
      return val
    },
    remarkformat(val){
      if(val == null){
        val = '喜欢期货，所以我用期达人'
      }else{
        if(val.length>15){
          val = val.substr(0,15)+'...'
        }
      }
      return val
    },
    phoneformat(val){
      if(val == null){
        val = "未知"
      }else{
        val = val.substr(0,3)+"****"+val.substr(7)
      }
      return val
    },
    onSelect(item){
      let model = {
